mapper.select:
select userId, name, age, gender, joinDate, pass, profile
from member
where userId = #{userId}
and pass = #{pass}
and enable = 1

select count(*)
from member
where userId = #{userId}

mapper.update:
update member
set pass = #{pass},
age = #{age},
gender = #{gender},
profile = #{profile}
where userId = #{userId}

update member
set pass = #{newpass},
age = #{age},
gender = #{gender},
profile = #{profile}
where userId = #{userId}

update member
set enable = 0
where userId = #{userId}

mapper.select:
 		select count(*) reviewcnt
		from review
		where userid = #{userId}
